                                                         StunAttribute type,
                                                         size_t length);

StunMessageReturn   stun_message_append32               (StunMessage *msg,
                                                         StunAttribute type,
                                                         uint32_t value);

StunMessageReturn   stun_message_append64               (StunMessage *msg,
                                                         StunAttribute type,
                                                         uint64_t value);

StunMessageReturn   stun_message_append_string          (StunMessage *msg,
                                                         StunAttribute type,
                                                         const char *str);
